import React, { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = () => {

    const [filteredYear, setFilteredYears] = useState()

    function filterHandler(event) {
        if(event.target.value === "2022") {
            setFilteredYears('2022')
        } 
        if(event.target.value === "2021") {
            setFilteredYears('2021')
        }
        if(event.target.value === "2020") {
            setFilteredYears('2020')
        }
        if(event.target.value === "2019") {
            setFilteredYears('2019')
        }
    }

    console.log(filteredYears)

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onChange={filterHandler}>
          <option value='2022'>2022</option>
          <option value='2021'>2021</option>
          <option value='2020'>2020</option>
          <option value='2019'>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
