import Card from "../UI/Card"
import ExpenseItem from "./ExpenseItem"
import './Expenses.css'
import ExpensesFilter from "./ExpensesFilter"

function Expenses({expenses, filterHandler}) {

  const [filteredYear, setFilteredYear] = useState()

  function filterHandler(event) {
    if(event.target.value === "2022") {
        setFilteredYear('2022')
    } 
    if(event.target.value === "2021") {
        setFilteredYear('2021')
    }
    if(event.target.value === "2020") {
        setFilteredYear('2020')
    }
    if(event.target.value === "2019") {
        setFilteredYear('2019')
    }
}

  return (
    <Card className="expenses">
        <ExpensesFilter onChange={filterHandler} />
        <ExpenseItem expenses={expenses} />
    </Card>
  )
}

export default Expenses
